import dotenv from "dotenv";

dotenv.config();

const PORT = Number(process.env.PORT) || 1337;
const {
    NODE_ENV = "development",
    PROTOCOL_PREFIX = "mongodb+srv://",
    MONGO_SERVER_HOSTNAME = "cluster0.dptqijs.mongodb.net",
    MONGO_USERNAME = "username123",
    MONGO_PASSWORD = "password123",

    ACCESS_TOKEN_SEED =
        "",
    REFRESH_TOKEN_SEED =
        "",
} = process.env;

const MONGO_URI =
    `${PROTOCOL_PREFIX}${MONGO_USERNAME}:${MONGO_PASSWORD}@${MONGO_SERVER_HOSTNAME}`;

type Config = {
    NODE_ENV: string;
    PORT: number;
    MONGO_URI: string;
    ACCESS_TOKEN_SEED: string;
    REFRESH_TOKEN_SEED: string;
};

const CONFIG: Config = {
    NODE_ENV,
    PORT,
    MONGO_URI,
    ACCESS_TOKEN_SEED,
    REFRESH_TOKEN_SEED,
};

export { CONFIG };
export type { Config };
